# Reference FGFR2 protein sequence
reference_protein = ""

# Mutations in format: (position, new_aa) -- no original amino acid provided
mutations = [
    (344, "G"),  # becomes A344G internally
    #(342, "R"),  # becomes C342R
    #(315, "S"),  # becomes A315S
    #(290, "G"),  # becomes W290G
]

# Convert reference sequence to list to make it mutable
mutant_protein = list(reference_protein)

# Apply mutations and track details
print("Mutations applied:")
for pos, new_aa in mutations:
    index = pos - 1  # Convert to 0-based indexing
    original_aa = mutant_protein[index]
    mutation_id = f"{original_aa}{pos}{new_aa}"
    print(f"  - {mutation_id}")
    mutant_protein[index] = new_aa  # Replace with new amino acid

# Join and output final mutant protein sequence
mutated_sequence = ''.join(mutant_protein)

print("\nMutated sequence:")
print(mutated_sequence)
